client/src/pages/job-details.tsx.content:
import { ArrowLeft, ExternalLink, Trash2 } from 'lucide-react';
import { useLocation, useParams } from 'wouter';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import { jobsApi, Job } from '@/lib/api';
import { useToast } from '@/hooks/use-toast';

export default function JobDetails() {
  const { id } = useParams<{ id: string }>();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: jobs } = useQuery<Job[]>({
    queryKey: ['/api/jobs']
  });

  const job = jobs?.find(j => j.id === parseInt(id || '0'));

  const deleteJobMutation = useMutation({
    mutationFn: jobsApi.delete,
    onSuccess: () => {
      toast({
        title: "Job deleted",
        description: "The job has been removed from your list.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/jobs'] });
      queryClient.invalidateQueries({ queryKey: ['/api/stats'] });
      setLocation('/jobs');
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete job.",
        variant: "destructive",
      });
    }
  });

  const applyJobMutation = useMutation({
    mutationFn: jobsApi.apply,
    onSuccess: () => {
      toast({
        title: "Application tracked",
        description: "Job status updated to Applied.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/jobs'] });
      queryClient.invalidateQueries({ queryKey: ['/api/stats'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update job status.",
        variant: "destructive",
      });
    }
  });

  const handleApply = () => {
    if (job?.url) {
      window.open(job.url, '_blank');
      applyJobMutation.mutate(job.id);
    }
  };

  const handleDelete = () => {
    if (job && confirm('Are you sure you want to delete this job?')) {
      deleteJobMutation.mutate(job.id);
    }
  };

  if (!job) {
    return (
      <div className="p-4 pb-20">
        <div className="flex items-center mb-6">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation('/jobs')}
            data-testid="button-back"
            className="mr-3"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <h1 className="text-xl font-bold text-foreground">Job Details</h1>
        </div>
        <Card>
          <CardContent className="p-6 text-center">
            <p className="text-muted-foreground">Job not found</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  const formatDate = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="p-4 pb-20">
      <div className="flex items-center mb-6">
        <Button
          variant="ghost"
          size="icon"
          onClick={() => setLocation('/jobs')}
          data-testid="button-back"
          className="mr-3"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <h1 className="text-xl font-bold text-foreground">Job Details</h1>
      </div>

      <Card className="mb-6">
        <CardContent className="p-6">
          {/* Job Header */}
          <div className="mb-6">
            <h2 className="text-2xl font-bold text-foreground mb-2" data-testid="text-job-title">
              {job.title}
            </h2>
            <div className="flex items-center space-x-4 text-muted-foreground mb-4">
              <span data-testid="text-company-name">{job.companyName}</span>
              <span>â€¢</span>
              <span data-testid="text-date-found">{formatDate(job.dateFound)}</span>
            </div>
            {job.matchedKeywords && job.matchedKeywords.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {job.matchedKeywords.map((keyword, index) => (
                  <Badge key={index} className="text-xs bg-primary text-primary-foreground">
                    {keyword}
                  </Badge>
                ))}
              </div>
            )}
          </div>

          {/* Job Details */}
          <div className="space-y-4 mb-6">
            {job.salary && (
              <div>
                <h3 className="font-semibold text-foreground mb-2">Salary</h3>
                <p className="text-muted-foreground" data-testid="text-salary">{job.salary}</p>
              </div>
            )}

            {job.appliedAt && (
              <div>
                <h3 className="font-semibold text-foreground mb-2">Application Date</h3>
                <p className="text-muted-foreground" data-testid="text-applied-date">
                  {formatDate(job.appliedAt)}
                </p>
              </div>
            )}

            {job.description && (
              <div>
                <h3 className="font-semibold text-foreground mb-2">Job Description</h3>
                <div className="text-muted-foreground space-y-2" data-testid="text-description">
                  <p>{job.description}</p>
                </div>
              </div>
            )}

            {job.requirements && job.requirements.length > 0 && (
              <div>
                <h3 className="font-semibold text-foreground mb-2">Requirements</h3>
                <ul className="text-muted-foreground space-y-1">
                  {job.requirements.map((req, index) => (
                    <li key={index}>â€¢ {req}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="flex flex-col space-y-3">
            <Button 
              onClick={handleApply}
              disabled={applyJobMutation.isPending}
              data-testid="button-apply"
              className="w-full py-3"
            >
              <ExternalLink className="mr-2 h-4 w-4" />
              {applyJobMutation.isPending ? 'Updating...' : 'Apply for Job'}
            </Button>

            <Button 
              variant="destructive"
              onClick={handleDelete}
              disabled={deleteJobMutation.isPending}
              data-testid="button-delete"
              className="w-full py-3"
            >
              <Trash2 className="mr-2 h-4 w-4" />
              {deleteJobMutation.isPending ? 'Deleting...' : 'Delete Job'}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/jobs-list.tsx.content:
import { ArrowLeft } from 'lucide-react';
import { useLocation, useSearch } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { JobCard } from '@/components/job-card';
import { Job } from '@/lib/api';

export default function JobsList() {
  const [, setLocation] = useLocation();
  const search = useSearch();
  const urlParams = new URLSearchParams(search);
  const filter = urlParams.get('filter') || 'all';

  const { data: jobs, isLoading } = useQuery<Job[]>({
    queryKey: ['/api/jobs']
  });

  const filteredJobs = jobs?.filter(job => {
    switch (filter) {
      case 'searched':
        return job.matchedKeywords && job.matchedKeywords.length > 0;
      case 'applied':
        return job.status === 'Applied';
      case 'recent':
        const dayAgo = new Date(Date.now() - 24 * 60 * 60 * 1000);
        return new Date(job.dateFound) > dayAgo;
      default:
        return true;
    }
  }) || [];

  const handleJobClick = (jobId: number) => {
    setLocation(`/job/${jobId}`);
  };

  const handleFilterChange = (newFilter: string) => {
    setLocation(`/jobs?filter=${newFilter}`);
  };

  if (isLoading) {
    return (
      <div className="p-4 pb-20">
        <div className="space-y-4">
          <div className="h-8 bg-muted rounded animate-pulse"></div>
          <div className="h-12 bg-muted rounded animate-pulse"></div>
          {[...Array(5)].map((_, i) => (
            <div key={i} className="h-24 bg-muted rounded animate-pulse"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 pb-20">
      <div className="flex items-center mb-6">
        <Button
          variant="ghost"
          size="icon"
          onClick={() => setLocation('/')}
          data-testid="button-back"
          className="mr-3"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <h1 className="text-xl font-bold text-foreground">All Jobs</h1>
      </div>

      {/* Filter Tabs */}
      <div className="flex space-x-1 mb-6 bg-muted p-1 rounded-lg">
        <Button
          variant={filter === 'all' ? 'default' : 'ghost'}
          onClick={() => handleFilterChange('all')}
          data-testid="filter-all"
          className="flex-1 py-2 px-4"
        >
          All Jobs
        </Button>
        <Button
          variant={filter === 'searched' ? 'default' : 'ghost'}
          onClick={() => handleFilterChange('searched')}
          data-testid="filter-searched"
          className="flex-1 py-2 px-4"
        >
          Searched
        </Button>
        <Button
          variant={filter === 'applied' ? 'default' : 'ghost'}
          onClick={() => handleFilterChange('applied')}
          data-testid="filter-applied"
          className="flex-1 py-2 px-4"
        >
          Applied
        </Button>
      </div>

      {/* Jobs List */}
      <div className="space-y-4">
        {filteredJobs.length === 0 ? (
          <Card>
            <CardContent className="p-6 text-center">
              <p className="text-muted-foreground">No jobs found for the selected filter.</p>
            </CardContent>
          </Card>
        ) : (
          filteredJobs.map((job) => (
            <JobCard
              key={job.id}
              job={job}
              onClick={() => handleJobClick(job.id)}
            />
          ))
        )}
      </div>
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from 'react';
import { Link } from 'wouter';
import { Briefcase, Eye, EyeOff } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent } from '@/components/ui/card';
import { useAuth } from '@/hooks/use-auth';
import { useToast } from '@/hooks/use-toast';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      await login(email, password);
      toast({
        title: "Welcome back!",
        description: "You have been successfully logged in.",
      });
    } catch (error: any) {
      toast({
        title: "Login failed",
        description: error.message || "Invalid email or password.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-background">
      <div className="w-full max-w-md space-y-8">
        {/* Logo/Brand */}
        <div className="text-center">
          <div className="mx-auto h-16 w-16 bg-primary rounded-lg flex items-center justify-center mb-4">
            <Briefcase className="h-8 w-8 text-primary-foreground" />
          </div>
          <h1 className="text-2xl font-bold text-foreground">JobTracker</h1>
          <p className="text-muted-foreground">Track your dream jobs effortlessly</p>
        </div>

        {/* Login Form */}
        <Card>
          <CardContent className="p-6">
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="email" className="block text-sm font-medium text-foreground mb-1">
                  Email
                </Label>
                <Input
                  id="email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  required
                  data-testid="input-email"
                />
              </div>

              <div>
                <Label htmlFor="password" className="block text-sm font-medium text-foreground mb-1">
                  Password
                </Label>
                <div className="relative">
                  <Input
                    id="password"
                    type={showPassword ? 'text' : 'password'}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter your password"
                    required
                    data-testid="input-password"
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="icon"
                    className="absolute right-2 top-1/2 -translate-y-1/2 h-8 w-8"
                    onClick={() => setShowPassword(!showPassword)}
                    data-testid="button-toggle-password"
                  >
                    {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </Button>
                </div>
              </div>

              <Button 
                type="submit"
                className="w-full"
                disabled={isLoading}
                data-testid="button-login"
              >
                {isLoading ? 'Signing In...' : 'Sign In'}
              </Button>

              <div className="text-center">
                <Link href="/signup">
                  <Button variant="link" className="text-primary hover:text-primary/80 text-sm">
                    Don't have an account? Sign up
                  </Button>
                </Link>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

